mapper.select:
SELECT A.stdy_comment_id , A.stdy_id , A.stdy_comment_con , A.stdy_parent_comment_id , A.del_yn , A.rgsn_user_id , RGSN.user_nm AS rgsn_user_nm , A.rgsn_ts , A.amnn_user_id , AMNN.user_nm AS amnn_user_nm , A.amnn_ts
FROM stdy_comment_l A
/* 등록/수정 사용자 정보 */ LEFT JOIN stdy_user_info_m RGSN ON RGSN.user_id = A.rgsn_user_id LEFT JOIN stdy_user_info_m AMNN ON AMNN.user_id = A.amnn_user_id
WHERE 1 = 1
AND A.stdy_id = #{stdyId}
/* 부모 ID 가 검색조건으로 존재하는 경우, 부모ID 검색 그렇지 않은 경우 부모ID 가 NULL 인 댓글 조회*/
AND A.stdy_parent_comment_id = #{stdyParentCommentId}
AND A.stdy_parent_comment_id IS NULL
ORDER BY A.rgsn_ts
LIMIT #{record} OFFSET (#{page} - 1)

mapper.insert:
INSERT INTO stdy_comment_l
(
    stdy_comment_id
    , stdy_id
    , stdy_comment_con
    , stdy_parent_comment_id
    , del_yn
    , rgsn_user_id
    , rgsn_ts
    , amnn_user_id
    , amnn_ts

)
VALUES
(
    #{stdyCommentId}
    , #{stdyId}
    , #{stdyCommentCon}
    , #{stdyParentCommentId}
    , 'N'
    , #{rgsnUserId}
    , CURRENT_TIMESTAMP
    , #{amnnUserId}
    , CURRENT_TIMESTAMP
)

mapper.select:
SELECT A.stdy_com_comment_id , A.stdy_com_id , A.stdy_com_comment_con , A.stdy_parent_com_comment_id , A.del_yn , A.rgsn_user_id , RGSN.user_nm AS rgsn_user_nm , A.rgsn_ts , A.amnn_user_id , AMNN.user_nm AS amnn_user_nm , A.amnn_ts FROM stdy_com_comment_l A
/* 등록/수정 사용자 정보 */ LEFT JOIN stdy_user_info_m RGSN ON RGSN.user_id = A.rgsn_user_id LEFT JOIN stdy_user_info_m AMNN ON AMNN.user_id = A.amnn_user_id
WHERE 1 = 1
AND A.stdy_com_id = #{stdyComId}
/* 부모 ID 가 검색조건으로 존재하는 경우, 부모ID 검색 그렇지 않은 경우 부모ID 가 NULL 인 댓글 조회*/
AND A.stdy_parent_com_comment_id = #{stdyParentComCommentId}
AND A.stdy_parent_com_comment_id IS NULL
ORDER BY A.rgsn_ts
LIMIT #{record} OFFSET (#{page} - 1)

mapper.insert:
INSERT INTO stdy_com_comment_l
(
    stdy_com_comment_id
    , stdy_com_id
    , stdy_com_comment_con
    , stdy_parent_com_comment_id
    , del_yn
    , rgsn_user_id
    , rgsn_ts
    , amnn_user_id
    , amnn_ts
)
VALUES
(
    #{stdyComCommentId}
    , #{stdyComId}
    , #{stdyComCommentCon}
    , #{stdyParentComCommentId}
    , 'N'
    , #{rgsnUserId}
    , CURRENT_TIMESTAMP
    , #{amnnUserId}
    , CURRENT_TIMESTAMP
)
