mapper.select:
SELECT A.stdy_id , A.stdy_nm , A.stdy_con , TO_CHAR(A.stdy_st_dt, 'YYYY-MM-DD HH24:MI') AS stdy_st_dt , TO_CHAR(A.stdy_en_dt, 'YYYY-MM-DD HH24:MI') AS stdy_en_dt , A.stdy_st
, A.stdy_cat_id , B.stdy_cat_nm
, A.del_yn , A.rgsn_user_id , RGSN.user_nm AS rgsn_user_nm , A.rgsn_ts , A.amnn_user_id , AMNN.user_nm AS amnn_user_nm , A.amnn_ts
, coalesce(C.stdy_views_cnt, 0) AS stdy_views_cnt , coalesce(D.stdy_comment_cnt, 0) AS stdy_comment_cnt
FROM stdy_info_m A
/* 스터디 카테고리 정보 */ LEFT JOIN stdy_cat_info_m B ON B.stdy_cat_id = A.stdy_cat_id AND B.del_yn = 'N'
/* 스터디 방문자 목록 -> 방문자 수 */ LEFT JOIN ( SELECT stdy_id, COUNT(0) AS stdy_views_cnt FROM stdy_views_l GROUP BY stdy_id ) C ON C.stdy_id = A.stdy_id
/* 스터디 댓글 목록 -> 댓글 수*/ LEFT JOIN ( SELECT stdy_id, COUNT(0) AS stdy_comment_cnt FROM stdy_comment_l GROUP BY stdy_id ) D ON D.stdy_id = A.stdy_id
/* 스터디 태그 목록 */ LEFT JOIN ( SELECT stdy_id FROM STDY_TAG_L WHERE 1 = 1 AND UPPER(stdy_tag_con) LIKE '%' || UPPER(#{searchCon}) || '%' GROUP BY stdy_id ) E ON E.stdy_id = A.stdy_id
/* 등록/수정 사용자 정보 */ LEFT JOIN stdy_user_info_m RGSN ON RGSN.user_id = A.rgsn_user_id LEFT JOIN stdy_user_info_m AMNN ON AMNN.user_id = A.amnn_user_id
WHERE 1 = 1 AND A.del_yn ='N'
AND A.stdy_cat_id = #{stdyCatId}
AND ( UPPER(A.stdy_nm) LIKE '%' || UPPER(#{searchCon}) || '%' OR E.stdy_id IS NOT NULL )
ORDER BY A.rgsn_ts DESC
ORDER BY coalesce(C.stdy_views_cnt, 0) ASC
ORDER BY coalesce(D.stdy_comment_cnt, 0) ASC

SELECT A.stdy_id , A.stdy_nm , A.stdy_con , TO_CHAR(A.stdy_st_dt, 'YYYY-MM-DD HH24:MI') AS stdy_st_dt , TO_CHAR(A.stdy_en_dt, 'YYYY-MM-DD HH24:MI') AS stdy_en_dt , A.stdy_st
, A.stdy_cat_id , B.stdy_cat_nm
, A.del_yn , A.rgsn_user_id , RGSN.user_nm AS rgsn_user_nm , A.rgsn_ts , A.amnn_user_id , AMNN.user_nm AS amnn_user_nm , A.amnn_ts
, coalesce(C.stdy_views_cnt, 0) AS stdy_views_cnt , coalesce(D.stdy_comment_cnt, 0) AS stdy_comment_cnt
FROM stdy_info_m A
/* 스터디 카테고리 정보 */ LEFT JOIN stdy_comment_l B ON B.stdy_cat_id = A.stdy_cat_id AND B.del_yn = 'N'
/* 스터디 방문자 목록 -> 방문자 수 */ LEFT JOIN ( SELECT stdy_id, COUNT(0) AS stdy_views_cnt FROM stdy_views_l GROUP BY stdy_id ) C ON C.stdy_id = A.stdy_id
/* 스터디 댓글 목록 -> 댓글 수*/ LEFT JOIN ( SELECT stdy_id, COUNT(0) AS stdy_comment_cnt FROM stdy_comment_l GROUP BY stdy_id ) D ON
/* 등록/수정 사용자 정보 */ LEFT JOIN stdy_user_info_m RGSN ON RGSN.user_id = A.rgsn_user_id LEFT JOIN stdy_user_info_m AMNN ON AMNN.user_id = A.amnn_user_id
WHERE 1 = 1 AND A.del_yn ='N' AND A.stdy_id = #{stdyId}

mapper.insert:
insert into stdy_info_m
(
    stdy_id
    , stdy_nm
    , stdy_con
    , stdy_st_dt
    , stdy_en_dt
    , stdy_st
    , stdy_cat_id
    , del_yn
    , rgsn_user_id
    , rgsn_ts
    , amnn_user_id
    , amnn_ts
)
values
(
    #{stdyId}
    , #{stdyNm}
    , #{stdyCon}
    , #{stdyStDtTm}
    , #{stdyEnDtTm}
    , #{stdySt}
    , #{stdyCatId}
    , 'N'
    , #{rgsnUserId}
    , CURRENT_TIMESTAMP
    , #{amnnUserId}
    , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE
    stdy_info_m
SET
    stdy_nm       = #{stdyNm}
    , stdy_con      = #{stdyCon}
    , stdy_st_dt    = #{stdyStDtTm}
    , stdy_en_dt    = #{stdyEnDtTm}
    , stdy_st       = #{stdySt}
    , stdy_cat_id   = #{stdyCatId}
    , amnn_user_id  = #{amnnUserId}
    , amnn_ts       = CURRENT_TIMESTAMP
WHERE stdy_id = #{stdyId}

mapper.select:
SELECT stdy_tag_id , stdy_id , stdy_tag_con , del_yn , rgsn_user_id , rgsn_ts , amnn_user_id , amnn_ts
FROM stdy_tag_l
WHERE 1 = 1 AND stdy_id = #{stdyId}

mapper.insert:
insert into stdy_tag_l
(
    stdy_tag_id
    , stdy_id
    , stdy_tag_con
    , del_yn
    , rgsn_user_id
    , rgsn_ts
    , amnn_user_id
    , amnn_ts
)
values
(
    #{stdyTagId}
    , #{stdyId}
    , #{stdyTagCon}
    , 'N'
    , #{rgsnUserId}
    , CURRENT_TIMESTAMP
    , #{amnnUserId}
    , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE
    stdy_tag_l
SET
    del_yn = 'Y'
    , amnn_user_id = #{amnnUserId}
    , amnn_ts = CURRENT_TIMESTAMP
WHERE 1 = 1
    AND stdy_id = #{stdyId}

        AND stdy_tag_id = #{stdyTagId}
