mapper.select:
SELECT stdy_tag_id , stdy_id , stdy_tag_con , del_yn , rgsn_user_id , rgsn_ts , amnn_user_id , amnn_ts
FROM stdy_tag_l
WHERE 1 = 1 AND stdy_id = #{stdyId}

mapper.insert:
insert into stdy_tag_l
(
  stdy_tag_id
, stdy_id
, stdy_tag_con
, del_yn
, rgsn_user_id
, rgsn_ts
, amnn_user_id
, amnn_ts
)
values
    (
      #{stdyTagId}
    , #{stdyId}
    , #{stdyTagCon}
    , 'N'
    , #{rgsnUserId}
    , CURRENT_TIMESTAMP
    , #{amnnUserId}
    , CURRENT_TIMESTAMP
    )

mapper.update:
UPDATE
stdy_tag_l
SET
del_yn = 'Y'
, amnn_user_id = #{amnnUserId}
, amnn_ts = CURRENT_TIMESTAMP
WHERE 1 = 1
AND stdy_id = #{stdyId}

    AND stdy_tag_id = #{stdyTagId}
